var levels = new FilteredElementCollector(doc).OfClass(typeof(Level)).Cast<Level>().OrderBy(l => l.Elevation).ToList();
Level level = levels.FirstOrDefault();
var wallTypes = new FilteredElementCollector(doc).OfClass(typeof(WallType)).Cast<WallType>().Where(w => w.Kind == WallKind.Basic).ToList();
WallType wt = wallTypes.FirstOrDefault();
if (level == null) throw new Exception("No Levels found.");
if (wt == null) throw new Exception("No Basic Wall Types found.");
double radius = 10; 
List<XYZ> points = new List<XYZ>();
for (int i = 0; i < 5; i++)
{
    double angle = 2 * Math.PI * i / 5 - Math.PI / 2;
    points.Add(new XYZ(radius * Math.Cos(angle), radius * Math.Sin(angle), 0));
    double innerAngle = angle + Math.PI / 5;
    points.Add(new XYZ(radius * 0.4 * Math.Cos(innerAngle), radius * 0.4 * Math.Sin(innerAngle), 0));
}
for (int i = 0; i < points.Count; i++)
{
    XYZ p1 = points[i];
    XYZ p2 = points[(i + 1) % points.Count];
    Line line = Line.CreateBound(p1, p2);
    Wall.Create(doc, line, wt.Id, level.Id, 10, 0, false, false);
}

var grids = new FilteredElementCollector(doc, doc.ActiveView.Id).OfClass(typeof(Grid)).Cast<Grid>().ToList();
if (grids.Count > 1)
{
    ReferenceArray refs = new ReferenceArray();
    foreach(var g in grids) refs.Append(new Reference(g));
    Line line = Line.CreateBound(new XYZ(0,0,0), new XYZ(100,0,0));
    doc.Create.NewDimension(doc.ActiveView, line, refs);
}
